CREATE TABLE entity(id integer primary key autoincrement, name TEXT, namee TEXT, namec TEXT,type integer, major integer, minor integer, domain integer, tabgrp TEXT, tabatt TEXT,glmaj integer,glmin integer,log integer,form TEXT);
CREATE TABLE fld(id integer PRIMARY KEY AUTOINCREMENT,sys TEXT(16) NOT NULL,tabname TEXT(32) NOT NULL,colname TEXT(32) NOT NULL,namec TEXT(64) NOT NULL,namee TEXT(64) DEFAULT NULL,isnum integer(1) NOT NULL,disporder integer(2) NOT NULL,disptype integer(2) NOT NULL,newedit integer(1) NOT NULL,editable integer(1) NOT NULL,nullable integer(1) NOT NULL,disprows integer(2) NOT NULL,minval TEXT(16) DEFAULT NULL,maxval TEXT(16) DEFAULT NULL,defval TEXT(32) DEFAULT NULL,minlen integer(4) NOT NULL DEFAULT '0',maxlen integer(4) NOT NULL DEFAULT '0',groupe TEXT(64) DEFAULT NULL,groupc TEXT(64) DEFAULT NULL,qrylevel integer(1) NOT NULL DEFAULT '1',bzfld integer(1) NOT NULL DEFAULT '0',ismcard integer(1) NOT NULL DEFAULT '0',pattern TEXT(128) DEFAULT NULL);
CREATE TABLE fldvalue(id integer primary key autoincrement,sys TEXT(16)                      NOT NULL,             tabname         TEXT(32)                      NOT NULL,             colname         TEXT(32)                      NOT NULL,              disporder       integer(5)                         NOT NULL,           dbvalue         TEXT(32)                      NOT NULL,            dispc           TEXT(128)                     NOT NULL,             dispe           TEXT(32)                          NULL              );
CREATE TABLE plugin(id integer primary key autoincrement,sys TEXT(16) NOT NULL,cata TEXT(32) DEFAULT NULL,name TEXT(64) NOT NULL,pname TEXT(32) DEFAULT NULL,titlee TEXT(64) NOT NULL,titlec TEXT(64) NOT NULL,closeable integer(2,0) NOT NULL,uitype integer(2,0) NOT NULL,widgets TEXT(1024) DEFAULT NULL,runn integer(5,0) NOT NULL,dir TEXT(64) NOT NULL,classurl TEXT(32) NOT NULL,icon TEXT(32) DEFAULT NULL,service TEXT(128) DEFAULT NULL,initpara TEXT(1024) DEFAULT NULL, height TEXT(64) NOT NULL,width TEXT(64) NOT NULL,modal integer(2,0) NOT NULL,type integer(2,0) NOT NULL);